mapper.select:
select no, member_no, author, sns_link, author_thumbnail, debut_year, create_date, author_msg, author_status
from author 
where author=#{param1}

  		select  no, member_no, author, sns_link, author_thumbnail, debut_year, create_date, author_msg, author_status
		from author
		where member_no = #{param1};

select no, member_no, author, sns_link, author_thumbnail, debut_year, create_date, author_msg, author_status
from author 
where member_no = #{member_no};

select no, member_no, author, sns_link, author_thumbnail, debut_year, create_date, author_msg from author
where author like '%${searchWord}%'
order by author asc limit ${recordPerPage} offset ${offsetIndex}
