mapper.select:
select no, kakao_id, email, nickname , grade, warning_count, thumbnail, status, create_date 
from member
where email=#{param1}

select no, kakao_id, email, nickname , grade, warning_count, thumbnail, status, create_date 
from member
where no=${param1}

select b.no no, b.author author, b.sns_link sns_link, b.author_thumbnail author_thumbnail, b.debut_year debut_year, b.create_date create_date from author_fan a join author b on a.member_no=${member_no} and a.author_no = b.no and b.author_status = 1
where b.author like '%${searchWord}%'
order by b.author limit ${recordPerPage} offset ${offsetIndex}
