mapper.select:
select no, member_no, movie_id, content, movie_title, score_star, poster_path, vote_count, write_date, spo_check, warning_count
from movie_review
where member_no=${member_no}

	and (movie_title like '%${searchWord}%' or content like '%${searchWord}%') 

order by write_date
limit ${recordPerPage} offset ${offsetIndex}
